mapper.update:
UPDATE asyn_cmd${tableIndex}

    status = #{asynUpdateParam.status},
    gmt_modify = now(),

        execute_num = execute_num + 1


        execute_num = execute_num - 1


        success_executers = #{asynUpdateParam.successExecutes}


where
status = #{asynUpdateParam.whereAsynStatus} and
biz_id = #{asynUpdateParam.bizId}

UPDATE asyn_cmd${tableIndex}
status = #{asynUpdateParam.status}, gmt_modify = now(),
execute_num = execute_num + 1
execute_num = execute_num - 1
where status = #{asynUpdateParam.whereAsynStatus} and biz_id IN
#{bizId}

mapper.select:
select

from asyn_cmd
where
status = #{status} and next_time <= #{executerTime}

    order by gmt_create

limit #{limit}

